import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  PointElement,
  LineElement,
  ArcElement,
} from 'chart.js';
import { Bar, Bubble, Line, Pie } from 'react-chartjs-2';
import { useNavigate } from 'react-router-dom';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const [budgets, setBudgets] = useState([]);

  const authConfig = {
    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
  };

  const isAuthenticated = localStorage.getItem('isAuthenticated');
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated !== 'true') {
      navigate('/login');
    }
  }, [isAuthenticated, navigate]);

  useEffect(() => {
    if (isAuthenticated === 'true') {
      axios.get('http://localhost:3000/api/v1/budget/budgets', authConfig)
        .then(response => {
          if (response.statusText === 'OK') {
            setBudgets(response.data.budgets);
          } else {
            console.error('Error fetching budgets', response.statusText);
          }
        })
        .catch(error => {
          console.error('Error', error.response.data.error);
        });
    }
  }, [isAuthenticated]);

  useEffect(() => {
    if (isAuthenticated === 'true') {
      axios.get('http://localhost:3000/api/v1/expense/expenses', authConfig)
        .then(response => {
          if (response.statusText === 'OK') {
            setExpenses(response.data.expenses);
          } else {
            console.error('Error fetching expenses', response.statusText);
          }
        })
        .catch(error => {
          console.error('Error', error.response.data.error);
        });
    }
  }, [isAuthenticated]);

  let categoryExpenses = {};
  const categoryData = [];
  const categoryLabels = [];

  const calculateExpensesByCategory = () => {
    budgets.forEach(budget => {
      categoryExpenses[budget.category] = { budget: budget.amount, expenses: 0 };
    });

    expenses.forEach(expense => {
      const { category, amount } = expense;
      if (categoryExpenses[category]) {
        categoryExpenses[category].expenses += amount;
      } else {
        categoryExpenses[category] = { budget: 0, expenses: amount };
      }
    });

    Object.entries(categoryExpenses).forEach(([key, value]) => {
      categoryLabels.push(key);
      categoryData.push({ category: key, budget: value.budget, expenses: value.expenses });
    });
  };

  const primaryColor = '#4A90E2'; // Consistent primary color

  return (
    <div className="dashboard-container">
      <h1 style={{ fontSize: '24px' }}>Financial Dashboard</h1>
      {calculateExpensesByCategory()}
      <div className="charts-container">
        <div className="chart chart-bar">
          <Bar
            options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
                title: {
                  display: true,
                  text: 'Comparison of Budget vs Expenses',
                },
              },
            }}
            data={{
              labels: categoryLabels,
              datasets: [
                {
                  label: 'Budget',
                  data: categoryData.map(data => data.budget),
                  backgroundColor: primaryColor,
                },
                {
                  label: 'Expenses',
                  data: categoryData.map(data => data.expenses),
                  backgroundColor: '#F76C6C',
                },
              ],
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
